var jade = require('jade'),
  fs = require('fs'),
  html = require("html");

fs.readFile('views/jade/editor.jade', 'utf8', function(err, data) {
  if (err) throw err;
  var fn = jade.compile(data);
  var _html = fn({
    css: function(route) {
      return "<link rel='style/css' href='/css/apps/editor.css'/>";
    },
    js: function() {
      return "<script src='/js/apps/editor.js'></script>";
    },
    "url": "",
    "state": ""
  });
  //console.log(html);
  console.log(html.prettyPrint(_html));
});
